contracts/Altan.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract Altan is ERC20, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    constructor() ERC20("Altan", "ALTAN") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function mint(address to, uint256 amount) external onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }
}

contracts/CentralBank.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./Altan.sol";

contract CentralBank is AccessControl {
    /// @dev Роль тех, кто имеет право вызывать эмиссию (TenRegistry, другие институты).
    bytes32 public constant ISSUER_ROLE = keccak256("ISSUER_ROLE");

    Altan public immutable altan;

    // политика (можно менять)
    uint256 public tenRewardPerSeat = 100 ether; // 100 ALTAN на seat после подтверждения десятки

    event TenRewardPerSeatUpdated(uint256 oldAmount, uint256 newAmount);
    event Issued(address indexed to, uint256 amount, address indexed issuer);

    error ZERO_ADDRESS();

    constructor(address altanToken) {
        if (altanToken == address(0)) revert ZERO_ADDRESS();
        altan = Altan(altanToken);

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);

        // На MVP: админ может эмитить напрямую.
        // Позже: можно не выдавать администратору ISSUER_ROLE, а выдавать только реестрам.
        _grantRole(ISSUER_ROLE, msg.sender);
    }

    function setTenRewardPerSeat(uint256 amount) external onlyRole(DEFAULT_ADMIN_ROLE) {
        emit TenRewardPerSeatUpdated(tenRewardPerSeat, amount);
        tenRewardPerSeat = amount;
    }

    /// @notice Общая эмиссия (в идеале только на BankAccount).
    function issue(address to, uint256 amount) external onlyRole(ISSUER_ROLE) {
        if (to == address(0)) revert ZERO_ADDRESS();
        altan.mint(to, amount);
        emit Issued(to, amount, msg.sender);
    }

    /// @notice Удобная функция: эмиссия награды за подтверждение десятки.
    function issueTenReward(address bankAccount) external onlyRole(ISSUER_ROLE) {
        if (bankAccount == address(0)) revert ZERO_ADDRESS();
        uint256 amount = tenRewardPerSeat;
        altan.mint(bankAccount, amount);
        emit Issued(bankAccount, amount, msg.sender);
    }
}

contracts/ElectionRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

interface ISeatSBT {
    function mintSeat(address to) external returns (uint256 seatId);
}

/**
 * Minimal ElectionRegistry demo:
 * - registerWinner(positionId, winner)
 * - finalize(positionId) mints SeatSBT to winner
 *
 * NOTE:
 * Здесь seatId НЕ задается извне, его выдаёт SeatSBT (nextId++).
 * Поэтому используем positionId (или electionId) как ключ реестра победителя.
 */
contract ElectionRegistry {
    address public immutable admin;
    ISeatSBT public immutable seat;

    // positionId/electionId -> winner
    mapping(uint256 => address) public winnerOf;

    error NOT_ADMIN();
    error WINNER_ALREADY_SET();
    error INVALID_WINNER();

    constructor(address seat_) {
        admin = msg.sender;
        seat = ISeatSBT(seat_);
    }

    function registerWinner(uint256 positionId, address winner) external {
        if (msg.sender != admin) revert NOT_ADMIN();
        if (winnerOf[positionId] != address(0)) revert WINNER_ALREADY_SET();
        if (winner == address(0)) revert INVALID_WINNER();
        winnerOf[positionId] = winner;
    }

    function finalize(uint256 positionId) external {
        if (msg.sender != admin) revert NOT_ADMIN();
        address winner = winnerOf[positionId];
        if (winner == address(0)) revert INVALID_WINNER();

        // mint SeatSBT to winner; SeatSBT returns newly created seatId
        seat.mintSeat(winner);
    }
}

contracts/SeatSBT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * SeatSBT = "цифровой гражданин" (Soulbound)
 * - не передается
 * - хранит статус
 * - хранит привязанный SeatTamga (смарт-кошелек)
 */
contract SeatSBT is ERC721, AccessControl {
    bytes32 public constant REGISTRAR_ROLE = keccak256("REGISTRAR_ROLE");

    enum Status {
        Draft,
        Verified10,
        VerifiedMarriage,
        Citizen
    }

    uint256 public nextId = 1;

    mapping(uint256 => Status) public statusOf;
    mapping(uint256 => address) public tamgaOf; // seatId -> SeatTamga

    constructor() ERC721("INOMAD Seat", "SEAT") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(REGISTRAR_ROLE, msg.sender);
    }

    function mintSeat(address to) external onlyRole(REGISTRAR_ROLE) returns (uint256 seatId) {
        seatId = nextId++;
        _safeMint(to, seatId);
        statusOf[seatId] = Status.Draft;
    }

    function setStatus(uint256 seatId, Status s) external onlyRole(REGISTRAR_ROLE) {
        require(_ownerOf(seatId) != address(0), "Seat: nonexistent");
        statusOf[seatId] = s;
    }

    function setTamga(uint256 seatId, address seatTamga) external onlyRole(REGISTRAR_ROLE) {
        require(_ownerOf(seatId) != address(0), "Seat: nonexistent");
        tamgaOf[seatId] = seatTamga;
    }

    function isCitizen(uint256 seatId) external view returns (bool) {
        return statusOf[seatId] == Status.Citizen;
    }

    // Soulbound: запрещаем трансферы
    function _update(address to, uint256 tokenId, address auth)
        internal
        override
        returns (address from)
    {
        from = _ownerOf(tokenId);
        // разрешаем mint (from == 0) и burn (to == 0), но запрещаем обычный transfer
        if (from != address(0) && to != address(0)) revert("SeatSBT: non-transferable");
        return super._update(to, tokenId, auth);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

contracts/SeatTamga.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/interfaces/IERC721.sol";

/**
 * SeatTamga = "печать/исполнитель/счет" для SeatSBT
 * - привязана к seatId (SeatSBT)
 * - controller = EOA (MVP)
 * Позже controller заменяем на Passkey/WebAuthn или AA / multisig.
 */
contract SeatTamga {
    using Address for address;

    address public immutable seatSbt;
    uint256 public immutable seatId;

    address public controller; // MVP: EOA

    error NotAuthorized();

    constructor(address _seatSbt, uint256 _seatId, address _controller) {
        seatSbt = _seatSbt;
        seatId = _seatId;
        controller = _controller;
    }

    modifier onlySeatOwnerOrController() {
        address seatOwner = IERC721(seatSbt).ownerOf(seatId);
        if (msg.sender != controller && msg.sender != seatOwner) revert NotAuthorized();
        _;
    }

    function setController(address newController) external onlySeatOwnerOrController {
        controller = newController;
    }

    function execute(address to, uint256 value, bytes calldata data)
        external
        onlySeatOwnerOrController
        returns (bytes memory)
    {
        return to.functionCallWithValue(data, value);
    }

    receive() external payable {}
}

contract SeatTamgaFactory {
    event TamgaCreated(uint256 indexed seatId, address tamga, address controller, bytes32 salt);

    address public immutable seatSbt;

    constructor(address _seatSbt) {
        seatSbt = _seatSbt;
    }

    function predictAddress(uint256 seatId, address controller, bytes32 salt) public view returns (address) {
        bytes memory bytecode = abi.encodePacked(
            type(SeatTamga).creationCode,
            abi.encode(seatSbt, seatId, controller)
        );
        bytes32 codeHash = keccak256(bytecode);
        return address(uint160(uint256(keccak256(abi.encodePacked(
            bytes1(0xff), address(this), salt, codeHash
        )))));
    }

    function createTamga(uint256 seatId, address controller, bytes32 salt) external returns (address tamga) {
        bytes memory bytecode = abi.encodePacked(
            type(SeatTamga).creationCode,
            abi.encode(seatSbt, seatId, controller)
        );
        assembly {
            tamga := create2(0, add(bytecode, 0x20), mload(bytecode), salt)
            if iszero(tamga) { revert(0, 0) }
        }
        emit TamgaCreated(seatId, tamga, controller, salt);
    }
}

contracts/TenRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/interfaces/IERC721.sol";

import "./SeatSBT.sol";
import "./SeatTamga.sol";
import "./CentralBank.sol";

contract TenRegistry is AccessControl {
    bytes32 public constant REGISTRAR_ROLE = keccak256("REGISTRAR_ROLE");

    SeatSBT public immutable seat;
    SeatTamgaFactory public immutable factory;
    CentralBank public immutable bank;

    struct Ten {
        uint256[10] seats;
        bool approved;
        address proposer;
    }

    Ten[] public tens;

    event TenProposed(uint256 indexed tenId, address indexed proposer);
    event TenApproved(uint256 indexed tenId);

    constructor(address seatSbt, address seatTamgaFactory, address centralBank) {
        seat = SeatSBT(seatSbt);
        factory = SeatTamgaFactory(seatTamgaFactory);
        bank = CentralBank(centralBank);

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(REGISTRAR_ROLE, msg.sender);
    }

    function proposeTen(uint256[10] calldata seatIds) external returns (uint256 tenId) {
        // на MVP: проверка минимальная — что seat существует
        for (uint256 i = 0; i < 10; i++) {
            require(IERC721(address(seat)).ownerOf(seatIds[i]) != address(0), "Seat nonexistent");
        }

        tens.push(Ten({
            seats: seatIds,
            approved: false,
            proposer: msg.sender
        }));

        tenId = tens.length - 1;
        emit TenProposed(tenId, msg.sender);
    }

    function approveTen(uint256 tenId) external onlyRole(REGISTRAR_ROLE) {
        Ten storage t = tens[tenId];
        require(!t.approved, "Already approved");

        for (uint256 i = 0; i < 10; i++) {
            uint256 seatId = t.seats[i];
            address owner = IERC721(address(seat)).ownerOf(seatId);

            // 1) создаем SeatTamga если нет
            address tamga = seat.tamgaOf(seatId);
            if (tamga == address(0)) {
                // salt = keccak256(...) (детерминированно)
                bytes32 salt = keccak256(abi.encodePacked("SEAT_TAMGA", seatId));
                tamga = factory.createTamga(seatId, owner, salt);
                seat.setTamga(seatId, tamga);
            }

            // 2) ставим статус Verified10
            seat.setStatus(seatId, SeatSBT.Status.Verified10);

            // 3) начисляем ALTAN на SeatTamga
            bank.issue(tamga, bank.tenRewardPerSeat());
        }

        t.approved = true;
        emit TenApproved(tenId);
    }

    function count() external view returns (uint256) {
        return tens.length;
    }
}

